import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';
import './Account.css';

const Account = () => {
  const { user, updateUser } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');
  
  // Profile
  const [username, setUsername] = useState('');
  
  // Password
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  
  // API Key
  const [apiKey, setApiKey] = useState('');
  const [apiSecret, setApiSecret] = useState('');
  const [isTestnet, setIsTestnet] = useState(false);
  const [hasApiKey, setHasApiKey] = useState(false);
  const [apiKeyInfo, setApiKeyInfo] = useState(null);
  
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (user) {
      setUsername(user.username);
    }
    checkApiKey();
  }, [user]);

  const checkApiKey = async () => {
    try {
      const response = await api.get('/apikey');
      setHasApiKey(response.data.hasApiKey);
      if (response.data.hasApiKey) {
        setApiKeyInfo(response.data.apiKey);
      }
    } catch (error) {
      console.error('API key check error:', error);
    }
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await api.put('/auth/profile', { username });
      updateUser({ username: response.data.user.username });
      toast.success('„Éó„É≠„Éï„Ç£„Éº„É´„ÇíÊõ¥Êñ∞„Åó„Åæ„Åó„Åü');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Êõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      toast.error('Êñ∞„Åó„ÅÑ„Éë„Çπ„ÉØ„Éº„Éâ„Åå‰∏ÄËá¥„Åó„Åæ„Åõ„Çì');
      return;
    }
    setLoading(true);
    try {
      await api.put('/auth/password', { currentPassword, newPassword });
      toast.success('„Éë„Çπ„ÉØ„Éº„Éâ„ÇíÂ§âÊõ¥„Åó„Åæ„Åó„Åü');
      setCurrentPassword('');
      setNewPassword('');
      setConfirmPassword('');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Â§âÊõ¥„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  const handleApiKeyRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post('/apikey', { apiKey, apiSecret, isTestnet });
      toast.success('API„Ç≠„Éº„ÇíÁôªÈå≤„Åó„Åæ„Åó„Åü');
      setApiKey('');
      setApiSecret('');
      checkApiKey();
    } catch (error) {
      toast.error(error.response?.data?.message || 'ÁôªÈå≤„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  const handleApiKeyDelete = async () => {
    if (!window.confirm('API„Ç≠„Éº„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºüËá™ÂãïÂèñÂºï„ÇÇÂÅúÊ≠¢„Åï„Çå„Åæ„Åô„ÄÇ')) return;
    setLoading(true);
    try {
      await api.delete('/apikey');
      toast.success('API„Ç≠„Éº„ÇíÂâäÈô§„Åó„Åæ„Åó„Åü');
      setHasApiKey(false);
      setApiKeyInfo(null);
      updateUser({ tradingEnabled: false });
    } catch (error) {
      toast.error(error.response?.data?.message || 'ÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  const handleApiKeyValidate = async () => {
    setLoading(true);
    try {
      const response = await api.post('/apikey/validate');
      if (response.data.isValid) {
        toast.success('API„Ç≠„Éº„ÅØÊúâÂäπ„Åß„Åô');
      } else {
        toast.warning('API„Ç≠„Éº„ÅåÁÑ°Âäπ„Åß„Åô„ÄÇÂÜçÁôªÈå≤„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ');
      }
      checkApiKey();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Ê§úË®º„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="account">
      <div className="page-header">
        <h1>„Ç¢„Ç´„Ç¶„É≥„ÉàË®≠ÂÆö</h1>
      </div>

      <div className="tabs">
        <button
          className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => setActiveTab('profile')}
        >
          „Éó„É≠„Éï„Ç£„Éº„É´
        </button>
        <button
          className={`tab ${activeTab === 'password' ? 'active' : ''}`}
          onClick={() => setActiveTab('password')}
        >
          „Éë„Çπ„ÉØ„Éº„ÉâÂ§âÊõ¥
        </button>
        <button
          className={`tab ${activeTab === 'apikey' ? 'active' : ''}`}
          onClick={() => setActiveTab('apikey')}
        >
          APIË®≠ÂÆö
        </button>
      </div>

      <div className="tab-content">
        {/* Profile Tab */}
        {activeTab === 'profile' && (
          <form onSubmit={handleProfileUpdate} className="account-form">
            <h2>„Éó„É≠„Éï„Ç£„Éº„É´ÊÉÖÂ†±</h2>
            
            <div className="form-group">
              <label>„É°„Éº„É´„Ç¢„Éâ„É¨„Çπ</label>
              <input type="email" value={user?.email || ''} disabled />
              <span className="hint">„É°„Éº„É´„Ç¢„Éâ„É¨„Çπ„ÅØÂ§âÊõ¥„Åß„Åç„Åæ„Åõ„Çì</span>
            </div>

            <div className="form-group">
              <label htmlFor="username">„É¶„Éº„Ç∂„ÉºÂêç</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>ÁôªÈå≤Êó•</label>
              <input
                type="text"
                value={user?.createdAt ? new Date(user.createdAt).toLocaleDateString('ja-JP') : ''}
                disabled
              />
            </div>

            <button type="submit" className="submit-btn" disabled={loading}>
              {loading ? 'Êõ¥Êñ∞‰∏≠...' : 'Êõ¥Êñ∞'}
            </button>
          </form>
        )}

        {/* Password Tab */}
        {activeTab === 'password' && (
          <form onSubmit={handlePasswordChange} className="account-form">
            <h2>„Éë„Çπ„ÉØ„Éº„ÉâÂ§âÊõ¥</h2>

            <div className="form-group">
              <label htmlFor="currentPassword">ÁèæÂú®„ÅÆ„Éë„Çπ„ÉØ„Éº„Éâ</label>
              <input
                type="password"
                id="currentPassword"
                value={currentPassword}
                onChange={(e) => setCurrentPassword(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="newPassword">Êñ∞„Åó„ÅÑ„Éë„Çπ„ÉØ„Éº„Éâ</label>
              <input
                type="password"
                id="newPassword"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                minLength={6}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="confirmPassword">Êñ∞„Åó„ÅÑ„Éë„Çπ„ÉØ„Éº„ÉâÔºàÁ¢∫Ë™çÔºâ</label>
              <input
                type="password"
                id="confirmPassword"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>

            <button type="submit" className="submit-btn" disabled={loading}>
              {loading ? 'Â§âÊõ¥‰∏≠...' : '„Éë„Çπ„ÉØ„Éº„Éâ„ÇíÂ§âÊõ¥'}
            </button>
          </form>
        )}

        {/* API Key Tab */}
        {activeTab === 'apikey' && (
          <div className="account-form">
            <h2>Bybit API„Ç≠„ÉºË®≠ÂÆö</h2>

            {hasApiKey ? (
              <div className="apikey-status">
                <div className="status-card">
                  <div className="status-header">
                    <span className="status-icon">üîë</span>
                    <span className="status-text">API„Ç≠„ÉºÁôªÈå≤Ê∏à„Åø</span>
                  </div>
                  <div className="status-body">
                    <div className="status-row">
                      <span className="label">Áí∞Â¢É</span>
                      <span className="value">{apiKeyInfo?.isTestnet ? '„ÉÜ„Çπ„Éà„Éç„ÉÉ„Éà' : 'Êú¨Áï™Áí∞Â¢É'}</span>
                    </div>
                    <div className="status-row">
                      <span className="label">„Çπ„ÉÜ„Éº„Çø„Çπ</span>
                      <span className={`value ${apiKeyInfo?.isValid ? 'valid' : 'invalid'}`}>
                        {apiKeyInfo?.isValid ? 'ÊúâÂäπ' : 'ÁÑ°Âäπ'}
                      </span>
                    </div>
                    <div className="status-row">
                      <span className="label">ÁôªÈå≤Êó•</span>
                      <span className="value">
                        {apiKeyInfo?.createdAt ? new Date(apiKeyInfo.createdAt).toLocaleDateString('ja-JP') : '-'}
                      </span>
                    </div>
                  </div>
                  <div className="status-actions">
                    <button className="validate-btn" onClick={handleApiKeyValidate} disabled={loading}>
                      Ê§úË®º
                    </button>
                    <button className="delete-btn" onClick={handleApiKeyDelete} disabled={loading}>
                      ÂâäÈô§
                    </button>
                  </div>
                </div>
                
                <div className="reregister-section">
                  <h3>API„Ç≠„Éº„ÇíÂÜçÁôªÈå≤</h3>
                  <p>Êñ∞„Åó„ÅÑAPI„Ç≠„Éº„Åß‰∏äÊõ∏„ÅçÁôªÈå≤„Åß„Åç„Åæ„Åô„ÄÇ</p>
                </div>
              </div>
            ) : (
              <div className="apikey-notice">
                <p>ÂèñÂºï„ÇíÈñãÂßã„Åô„Çã„Å´„ÅØ„ÄÅBybit„ÅÆAPI„Ç≠„Éº„ÇíÁôªÈå≤„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ</p>
                <p className="notice-detail">
                  ‚Äª API„Ç≠„Éº„Å´„ÅØ„ÄåTrade„ÄçÊ®©Èôê„ÅåÂøÖË¶Å„Åß„Åô„ÄÇ<br />
                  ‚Äª API„Ç≠„Éº„ÅØÊöóÂè∑Âåñ„Åó„Å¶‰øùÂ≠ò„Åï„Çå„Åæ„Åô„ÄÇ
                </p>
              </div>
            )}

            <form onSubmit={handleApiKeyRegister}>
              <div className="form-group">
                <label htmlFor="apiKey">API„Ç≠„Éº</label>
                <input
                  type="text"
                  id="apiKey"
                  value={apiKey}
                  onChange={(e) => setApiKey(e.target.value)}
                  placeholder="Bybit API„Ç≠„Éº„ÇíÂÖ•Âäõ"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="apiSecret">API„Ç∑„Éº„ÇØ„É¨„ÉÉ„Éà</label>
                <input
                  type="password"
                  id="apiSecret"
                  value={apiSecret}
                  onChange={(e) => setApiSecret(e.target.value)}
                  placeholder="Bybit API„Ç∑„Éº„ÇØ„É¨„ÉÉ„Éà„ÇíÂÖ•Âäõ"
                  required
                />
              </div>

              <div className="form-group checkbox-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={isTestnet}
                    onChange={(e) => setIsTestnet(e.target.checked)}
                  />
                  <span>„ÉÜ„Çπ„Éà„Éç„ÉÉ„ÉàÁí∞Â¢É„Çí‰ΩøÁî®</span>
                </label>
              </div>

              <button type="submit" className="submit-btn" disabled={loading}>
                {loading ? 'ÁôªÈå≤‰∏≠...' : 'API„Ç≠„Éº„ÇíÁôªÈå≤'}
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Account;
